name: Gitleaks Security Scan

on:
  push:
    branches:
      - main
      - develop
  pull_request:
    branches:
      - main
      - develop
  workflow_dispatch:
  schedule:
    # Run weekly scan on Mondays at 9 AM UTC
    - cron: '0 9 * * 1'

