  gitleaks:
    name: Scan for Secrets (Non-Blocking)
    runs-on: ubuntu-latest

    steps:
      - name: Checkout code
        uses: actions/checkout@v4
        with:
          fetch-depth: 0  # Fetch all history for all branches and tags

      - name: Run Gitleaks
        id: gitleaks
        continue-on-error: true  # Don't fail workflow - just report
        uses: gitleaks/gitleaks-action@v2
        env:
          GITHUB_TOKEN: ${{ secrets.GITHUB_TOKEN }}
          GITLEAKS_LICENSE: ${{ secrets.GITLEAKS_LICENSE }}  # Optional: for Gitleaks Pro
          GITLEAKS_ENABLE_COMMENTS: false
          GITLEAKS_NOTIFY_USER_LIST: ""

      - name: Upload Gitleaks Report
        if: always()  # Always upload, even if no findings
        uses: actions/upload-artifact@v4
