import Foundation

// MARK: - Direction

/// Represents a cardinal direction for navigation and operations
public enum Direction: CaseIterable, Sendable {
    case up, down, left, right

    /// The opposite direction
    public var opposite: Direction {
        switch self {
        case .up: return .down
        case .down: return .up
        case .left: return .right
        case .right: return .left
        }
    }

    /// Whether this is a horizontal direction (left/right)
    public var isHorizontal: Bool {
        self == .left || self == .right
    }

    /// Whether this is a vertical direction (up/down)
    public var isVertical: Bool {
        self == .up || self == .down
    }
}

// MARK: - Split Direction

/// Direction of a split in the layout tree
public enum SplitDirection: String, Codable, Sendable {
    case horizontal  // Children are arranged left-to-right
    case vertical    // Children are arranged top-to-bottom

    /// Toggle between horizontal and vertical
    public var toggled: SplitDirection {
        self == .horizontal ? .vertical : .horizontal
    }
}

// MARK: - CGRect Extensions

extension CGRect {
    /// The center point of the rectangle
    public var center: CGPoint {
        CGPoint(x: midX, y: midY)
    }

    /// Split this rectangle into two parts
    /// - Parameters:
    ///   - direction: Whether to split horizontally or vertically
    ///   - ratio: The ratio (0.0-1.0) determining the split point
    /// - Returns: A tuple of two rectangles (first, second)
    public func split(_ direction: SplitDirection, ratio: CGFloat = 0.5) -> (first: CGRect, second: CGRect) {
        let clampedRatio = max(0.0, min(1.0, ratio))

        switch direction {
        case .horizontal:
            let splitX = origin.x + width * clampedRatio
            let first = CGRect(x: origin.x, y: origin.y, width: width * clampedRatio, height: height)
            let second = CGRect(x: splitX, y: origin.y, width: width * (1 - clampedRatio), height: height)
            return (first, second)

        case .vertical:
            let splitY = origin.y + height * clampedRatio
            let first = CGRect(x: origin.x, y: origin.y, width: width, height: height * clampedRatio)
            let second = CGRect(x: origin.x, y: splitY, width: width, height: height * (1 - clampedRatio))
            return (first, second)
        }
    }

    /// Create a new rectangle expanded by the given insets
    public func expanded(by insets: EdgeInsets) -> CGRect {
        CGRect(
            x: origin.x - insets.left,
            y: origin.y - insets.top,
            width: width + insets.left + insets.right,
            height: height + insets.top + insets.bottom
        )
    }

    /// Create a new rectangle contracted by the given insets
    public func contracted(by insets: EdgeInsets) -> CGRect {
        CGRect(
            x: origin.x + insets.left,
            y: origin.y + insets.top,
            width: max(0, width - insets.left - insets.right),
            height: max(0, height - insets.top - insets.bottom)
        )
    }

    /// Create a new rectangle with uniform padding
    public func inset(by padding: CGFloat) -> CGRect {
        insetBy(dx: padding, dy: padding)
    }

    /// Check if this rectangle is adjacent to another in the given direction
    public func isAdjacent(to other: CGRect, direction: Direction, tolerance: CGFloat = 1.0) -> Bool {
        switch direction {
        case .left:
            return abs(minX - other.maxX) <= tolerance &&
                   maxY > other.minY && minY < other.maxY
        case .right:
            return abs(maxX - other.minX) <= tolerance &&
                   maxY > other.minY && minY < other.maxY
        case .up:
            return abs(minY - other.maxY) <= tolerance &&
                   maxX > other.minX && minX < other.maxX
        case .down:
            return abs(maxY - other.minY) <= tolerance &&
                   maxX > other.minX && minX < other.maxX
        }
    }
}

// MARK: - EdgeInsets

/// Edge insets for padding/gaps
public struct EdgeInsets: Equatable, Codable, Sendable {
    public var top: CGFloat
    public var left: CGFloat
    public var bottom: CGFloat
    public var right: CGFloat

    public init(top: CGFloat = 0, left: CGFloat = 0, bottom: CGFloat = 0, right: CGFloat = 0) {
        self.top = top
        self.left = left
        self.bottom = bottom
        self.right = right
    }

    /// Create uniform insets
    public init(all: CGFloat) {
        self.top = all
        self.left = all
        self.bottom = all
        self.right = all
    }

    /// Create horizontal and vertical insets
    public init(horizontal: CGFloat, vertical: CGFloat) {
        self.top = vertical
        self.left = horizontal
        self.bottom = vertical
        self.right = horizontal
    }

    /// Zero insets
    public static let zero = EdgeInsets()
}

// MARK: - CGPoint Extensions

extension CGPoint {
    /// Distance to another point
    public func distance(to other: CGPoint) -> CGFloat {
        let dx = x - other.x
        let dy = y - other.y
        return sqrt(dx * dx + dy * dy)
    }

    /// The direction from this point to another
    public func direction(to other: CGPoint) -> Direction? {
        let dx = other.x - x
        let dy = other.y - y

        if abs(dx) > abs(dy) {
            return dx > 0 ? .right : .left
        } else if abs(dy) > abs(dx) {
            return dy > 0 ? .down : .up
        }
        return nil
    }
}

// MARK: - CGSize Extensions

extension CGSize {
    /// The aspect ratio (width / height)
    public var aspectRatio: CGFloat {
        guard height > 0 else { return 0 }
        return width / height
    }

    /// Whether this is a landscape size
    public var isLandscape: Bool {
        width > height
    }

    /// Whether this is a portrait size
    public var isPortrait: Bool {
        height > width
    }
}

