# MSYS2 Full Rebase Script
# This script rebases all MSYS2 DLLs to fix fork errors

Write-Host "=====================================" -ForegroundColor Cyan
Write-Host "MSYS2 Full Rebase Script" -ForegroundColor Cyan
Write-Host "=====================================" -ForegroundColor Cyan
Write-Host ""

# Step 1: Check if running as Administrator
$isAdmin = ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole]::Administrator)
if ($isAdmin) {
    Write-Host "[OK] Running as Administrator" -ForegroundColor Green
} else {
    Write-Host "[WARNING] Not running as Administrator. Rebase may have limited effectiveness." -ForegroundColor Yellow
}
Write-Host ""

# Step 2: Check for running processes
Write-Host "Step 1: Checking for running Git Bash and Claude processes..." -ForegroundColor Yellow
$bashProcesses = Get-Process | Where-Object {$_.Name -like "*bash*"}
$claudeProcesses = Get-Process | Where-Object {$_.Name -like "*claude*"}
$gitProcesses = Get-Process | Where-Object {$_.Name -like "*git*" -and $_.Name -ne "git-bash"}

if ($bashProcesses) {
    Write-Host "[FOUND] Bash processes running: $($bashProcesses.Count)" -ForegroundColor Red
    $bashProcesses | Select-Object Name, Id | Format-Table
}
if ($claudeProcesses) {
    Write-Host "[FOUND] Claude processes running: $($claudeProcesses.Count)" -ForegroundColor Red
    $claudeProcesses | Select-Object Name, Id | Format-Table
}
if ($gitProcesses) {
    Write-Host "[FOUND] Git processes running: $($gitProcesses.Count)" -ForegroundColor Red
    $gitProcesses | Select-Object Name, Id | Format-Table
}

if ($bashProcesses -or $claudeProcesses -or $gitProcesses) {
    Write-Host ""
    Write-Host "ERROR: MSYS2/Git Bash or Claude Code processes are still running!" -ForegroundColor Red
    Write-Host "Please close all Claude Code windows and Git Bash terminals, then run this script again." -ForegroundColor Red
    Write-Host ""
    Write-Host "Press any key to exit..."
    $null = $Host.UI.RawUI.ReadKey("NoEcho,IncludeKeyDown")
    exit 1
}

Write-Host "[OK] No conflicting processes found" -ForegroundColor Green
Write-Host ""

# Step 3: Run rebase
Write-Host "Step 2: Running MSYS2 rebase operation..." -ForegroundColor Yellow
Write-Host "This may take 30-60 seconds..." -ForegroundColor Gray
Write-Host ""

$gitBash = "C:\Program Files\Git\usr\bin\bash.exe"
if (Test-Path $gitBash) {
    $startTime = Get-Date

    # Run rebase with verbose output
    $process = Start-Process -FilePath $gitBash -ArgumentList "-c", "/usr/bin/rebaseall -v" -Wait -NoNewWindow -PassThru

    $endTime = Get-Date
    $duration = ($endTime - $startTime).TotalSeconds

    if ($process.ExitCode -eq 0) {
        Write-Host "[SUCCESS] Rebase completed successfully in $([math]::Round($duration, 2)) seconds" -ForegroundColor Green
    } else {
        Write-Host "[ERROR] Rebase failed with exit code: $($process.ExitCode)" -ForegroundColor Red
    }
} else {
    Write-Host "[ERROR] Git Bash not found at: $gitBash" -ForegroundColor Red
    Write-Host "Please verify your Git installation path." -ForegroundColor Red
    exit 1
}

Write-Host ""
Write-Host "=====================================" -ForegroundColor Cyan
Write-Host "Rebase Complete!" -ForegroundColor Green
Write-Host "=====================================" -ForegroundColor Cyan
Write-Host ""
Write-Host "Next steps:" -ForegroundColor Yellow
Write-Host "1. You can now restart Claude Code" -ForegroundColor White
Write-Host "2. Fork errors should be significantly reduced or eliminated" -ForegroundColor White
Write-Host ""
Write-Host "Press any key to exit..."
$null = $Host.UI.RawUI.ReadKey("NoEcho,IncludeKeyDown")

