def sync_jsonl_logs():
    """Sync and redact .jsonl conversation logs."""
    if not CLAUDE_PROJECTS.exists():
        print(f"Warning: Source directory not found: {CLAUDE_PROJECTS}")
        return

    os.makedirs(TRANSCRIPTS_DEST, exist_ok=True)

    jsonl_files = list(CLAUDE_PROJECTS.glob("*.jsonl"))
    print(f"Found {len(jsonl_files)} conversation logs in {CLAUDE_PROJECTS}")

    synced_count = 0
    updated_count = 0

    for src_file in jsonl_files:
        dest_file = TRANSCRIPTS_DEST / src_file.name

        try:
            # Read source file line by line (JSONL format)
            redacted_lines = []
            with open(src_file, 'r', encoding='utf-8') as f:
                for line in f:
                    if line.strip():
                        try:
                            data = json.loads(line)
                            redacted_data = redact_json_structure(data)
                            redacted_lines.append(json.dumps(redacted_data, ensure_ascii=False))
                        except json.JSONDecodeError as e:
                            print(f"Warning: Skipping malformed JSON in {src_file.name}: {e}")
                            continue

            # Check if we need to update
            should_write = True
            if dest_file.exists():
                with open(dest_file, 'r', encoding='utf-8') as f:
                    existing_content = f.read()
                new_content = '\n'.join(redacted_lines) + '\n'
                if existing_content == new_content:
                    should_write = False

            if should_write:
                # Write redacted content
                with open(dest_file, 'w', encoding='utf-8') as f:
                    f.write('\n'.join(redacted_lines) + '\n')

                if dest_file.exists() and os.path.getsize(dest_file) > 0:
                    updated_count += 1
                else:
                    synced_count += 1

        except Exception as e:
            print(f"Error processing {src_file.name}: {e}")

