use anyhow::{Context, Result};
use std::path::{Path, PathBuf};
use std::time::Instant;

const RAPL_BASE_PATH: &str = "/sys/devices/virtual/powercap/intel-rapl";
const MAX_ENERGY_RANGE_UJ: u64 = 262_000_000; // Typical max range in microjoules

#[derive(Debug, Clone, Default)]
pub struct CpuPower {
    pub package_watts: f64,
    pub core_watts: f64,
    pub uncore_watts: f64,
}

pub struct RaplReader {
    package_path: PathBuf,
    core_path: PathBuf,
    uncore_path: PathBuf,
    last_readings: Option<(u64, u64, u64, Instant)>,
    max_energy_range: u64,
    enabled: bool,
}

impl RaplReader {
    /// Create a new RAPL reader, discovering paths automatically
    pub fn new() -> Result<Self> {
        let package_path = PathBuf::from(format!("{}/intel-rapl:0/energy_uj", RAPL_BASE_PATH));
        let core_path = PathBuf::from(format!("{}/intel-rapl:0/intel-rapl:0:0/energy_uj", RAPL_BASE_PATH));
        let uncore_path = PathBuf::from(format!("{}/intel-rapl:0/intel-rapl:0:1/energy_uj", RAPL_BASE_PATH));

        // Check if package path exists
        if !package_path.exists() {
            anyhow::bail!("RAPL not available: {} does not exist", package_path.display());
        }

        // Try to read max energy range
        let max_range_path = PathBuf::from(format!("{}/intel-rapl:0/max_energy_range_uj", RAPL_BASE_PATH));
        let max_energy_range = if max_range_path.exists() {
            std::fs::read_to_string(&max_range_path)
                .ok()
                .and_then(|s| s.trim().parse().ok())
                .unwrap_or(MAX_ENERGY_RANGE_UJ)
        } else {
            MAX_ENERGY_RANGE_UJ
        };

        tracing::info!("RAPL initialized with max_energy_range: {} J", max_energy_range);

        Ok(Self {
            package_path,
            core_path,
            uncore_path,
            last_readings: None,
            max_energy_range,
            enabled: true,
        })
    }

    /// Create a disabled RAPL reader (returns zero power)
    pub fn disabled() -> Self {
        Self {
            package_path: PathBuf::new(),
            core_path: PathBuf::new(),
            uncore_path: PathBuf::new(),
            last_readings: None,
            max_energy_range: MAX_ENERGY_RANGE_UJ,
            enabled: false,
        }
    }

    /// Read current power consumption
    pub async fn read_power(&mut self) -> Result<CpuPower> {
        if !self.enabled {
            return Ok(CpuPower::default());
        }

        let now = Instant::now();

        // Read energy counters
        let package = read_energy_counter(&self.package_path).await
            .context("Failed to read package energy")?;

        let core = read_energy_counter(&self.core_path).await
            .unwrap_or(0); // Core/uncore are optional

        let uncore = read_energy_counter(&self.uncore_path).await
            .unwrap_or(0);

        let power = if let Some((last_pkg, last_core, last_uncore, last_time)) = self.last_readings {
            let dt = now.duration_since(last_time).as_secs_f64();

            if dt < 0.001 {
                // Too soon, return previous power estimate
                return Ok(CpuPower::default());
            }

            // Calculate deltas, handling counter overflow
            let pkg_delta = calculate_energy_delta(package, last_pkg, self.max_energy_range);
            let core_delta = calculate_energy_delta(core, last_core, self.max_energy_range);
            let uncore_delta = calculate_energy_delta(uncore, last_uncore, self.max_energy_range);

            CpuPower {
                package_watts: (pkg_delta as f64 / 1_000_000.0) / dt,
                core_watts: (core_delta as f64 / 1_000_000.0) / dt,
                uncore_watts: (uncore_delta as f64 / 1_000_000.0) / dt,
            }
        } else {
            // First reading, no delta available yet
            CpuPower::default()
        };

        self.last_readings = Some((package, core, uncore, now));

        Ok(power)
    }
}

/// Read an energy counter from sysfs
async fn read_energy_counter(path: &Path) -> Result<u64> {
    let content = tokio::fs::read_to_string(path).await
        .with_context(|| format!("Failed to read {}", path.display()))?;

    content.trim()
        .parse()
        .with_context(|| format!("Failed to parse energy value from {}", path.display()))
}

/// Calculate energy delta, handling counter overflow
fn calculate_energy_delta(current: u64, previous: u64, max_range: u64) -> u64 {
    if current >= previous {
        current - previous
    } else {
        // Counter overflowed
        (max_range - previous) + current
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_energy_delta_normal() {
        assert_eq!(calculate_energy_delta(1000, 500, 262_000_000), 500);
        assert_eq!(calculate_energy_delta(5000, 1000, 262_000_000), 4000);
    }

    #[test]
    fn test_energy_delta_overflow() {
        let max = 262_000_000;
        // Counter was at (max - 50), now at 100
        // Should be: (max - (max - 50)) + 100 = 50 + 100 = 150
        let result = calculate_energy_delta(100, max - 50, max);
        assert_eq!(result, 150);
    }

    #[test]
    fn test_energy_delta_exact_overflow() {
        let max = 1000;
        assert_eq!(calculate_energy_delta(0, 1000, max), 0);
        assert_eq!(calculate_energy_delta(1, 999, max), 2);
    }

    #[tokio::test]
    async fn test_rapl_reader_creation() {
        // This will fail on systems without RAPL, which is expected
        match RaplReader::new() {
            Ok(_) => {
                tracing::info!("RAPL reader created successfully");
            }
            Err(e) => {
                tracing::warn!("RAPL not available: {}", e);
            }
        }
    }

    #[tokio::test]
    async fn test_disabled_reader() {
        let mut reader = RaplReader::disabled();
        let power = reader.read_power().await.unwrap();
        assert_eq!(power.package_watts, 0.0);
    }
}

