use anyhow::{Context, Result};

#[derive(Debug, Clone)]
pub enum NvidiaBackend {
    NvidiaSmi,
    Unavailable,
}

pub struct NvidiaReader {
    backend: NvidiaBackend,
}

impl NvidiaReader {
    /// Create a new NVIDIA reader
    pub fn new() -> Self {
        // For now, use nvidia-smi. We can add nvml-wrapper later if needed.
        // Check if nvidia-smi is available
        if Self::is_nvidia_smi_available() {
            tracing::info!("NVIDIA power monitoring enabled (nvidia-smi)");
            Self {
                backend: NvidiaBackend::NvidiaSmi,
            }
        } else {
            tracing::warn!("NVIDIA GPU not available or nvidia-smi not found");
            Self {
                backend: NvidiaBackend::Unavailable,
            }
        }
    }

    /// Check if nvidia-smi is available
    fn is_nvidia_smi_available() -> bool {
        std::process::Command::new("nvidia-smi")
            .arg("--help")
            .output()
            .map(|o| o.status.success())
            .unwrap_or(false)
    }

    /// Read current GPU power consumption in watts
    pub async fn read_power(&self) -> Result<f64> {
        match &self.backend {
            NvidiaBackend::NvidiaSmi => self.read_power_nvidia_smi().await,
            NvidiaBackend::Unavailable => {
                anyhow::bail!("NVIDIA GPU unavailable")
            }
        }
    }

    /// Read power using nvidia-smi command
    async fn read_power_nvidia_smi(&self) -> Result<f64> {
        let output = tokio::process::Command::new("nvidia-smi")
            .args(&[
                "--query-gpu=power.draw",
                "--format=csv,noheader,nounits",
                "--id=0",  // First GPU
            ])
            .output()
            .await
            .context("Failed to execute nvidia-smi")?;

        if !output.status.success() {
            let stderr = String::from_utf8_lossy(&output.stderr);
            anyhow::bail!("nvidia-smi failed: {}", stderr);
        }

        let stdout = String::from_utf8(output.stdout)
            .context("nvidia-smi output is not valid UTF-8")?;

        let watts: f64 = stdout.trim()
            .parse()
            .context("Failed to parse power value from nvidia-smi")?;

        Ok(watts)
    }
}

impl Default for NvidiaReader {
    fn default() -> Self {
        Self::new()
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_nvidia_reader_creation() {
        let reader = NvidiaReader::new();
        match &reader.backend {
            NvidiaBackend::NvidiaSmi => {
                tracing::info!("NVIDIA reader created with nvidia-smi backend");
            }
            NvidiaBackend::Unavailable => {
                tracing::info!("NVIDIA GPU not available");
            }
        }
    }

    #[tokio::test]
    async fn test_nvidia_power_read() {
        let reader = NvidiaReader::new();
        match reader.read_power().await {
            Ok(watts) => {
                tracing::info!("GPU Power: {:.2} W", watts);
                assert!(watts >= 0.0);
                assert!(watts < 1000.0); // Sanity check: should be less than 1kW
            }
            Err(e) => {
                tracing::warn!("Failed to read GPU power: {}", e);
            }
        }
    }
}

