use cosmic_applet_power_monitor::{RaplReader, NvidiaReader};
use std::time::Duration;
use tracing_subscriber;

#[tokio::main]
async fn main() -> anyhow::Result<()> {
    // Initialize logging
    tracing_subscriber::fmt()
        .with_env_filter("cosmic_applet_power_monitor=debug,info")
        .init();

    tracing::info!("Starting power monitor backend test...");

    // Initialize RAPL reader
    let mut rapl = match RaplReader::new() {
        Ok(reader) => {
            tracing::info!(" RAPL reader initialized successfully");
            reader
        }
        Err(e) => {
            tracing::error!(" RAPL initialization failed: {}", e);
            tracing::info!("  Tip: Try running with sudo or: sudo setcap cap_dac_read_search=ep target/debug/cosmic-applet-power-monitor");
            RaplReader::disabled()
        }
    };

    // Initialize NVIDIA reader
    let nvidia = NvidiaReader::new();

    tracing::info!("\nStarting power monitoring loop (Ctrl+C to exit)...\n");

    // Wait for first RAPL reading
    tokio::time::sleep(Duration::from_millis(500)).await;

    loop {
        // Read CPU power
        let cpu_power = match rapl.read_power().await {
            Ok(power) => power,
            Err(e) => {
                tracing::error!("RAPL read error: {}", e);
                tokio::time::sleep(Duration::from_millis(330)).await;
                continue;
            }
        };

        // Read GPU power
        let gpu_power = nvidia.read_power().await.ok();

        // Display results
        print!("\r");
        if cpu_power.package_watts > 0.0 {
            print!("CPU: {:.1}W (cores: {:.1}W, uncore: {:.1}W)",
                   cpu_power.package_watts,
                   cpu_power.core_watts,
                   cpu_power.uncore_watts);
        } else {
            print!("CPU: N/A");
        }

        if let Some(gpu_watts) = gpu_power {
            print!("  |  GPU: {:.1}W", gpu_watts);
        } else {
            print!("  |  GPU: N/A");
        }

        if cpu_power.package_watts > 0.0 && gpu_power.is_some() {
            let total = cpu_power.package_watts + gpu_power.unwrap_or(0.0) + 11.0; // +11W display estimate
            print!("  |  Total (est.): {:.1}W", total);
        }

        std::io::Write::flush(&mut std::io::stdout()).unwrap();

        // Wait for next reading (3 Hz = ~333ms)
        tokio::time::sleep(Duration::from_millis(330)).await;
    }
}

