/// Read an energy counter from sysfs
async fn read_energy_counter(path: &Path) -> Result<u64> {
    // Try direct read first
    match tokio::fs::read_to_string(path).await {
        Ok(content) => {
            content.trim()
                .parse()
                .with_context(|| format!("Failed to parse energy value from {}", path.display()))
        }
        Err(e) if e.kind() == std::io::ErrorKind::PermissionDenied => {
            // Fallback: use sudo cat
            let output = tokio::process::Command::new("sudo")
                .arg("cat")
                .arg(path)
                .output()
                .await
                .with_context(|| format!("Failed to read {} with sudo", path.display()))?;

            if !output.status.success() {
                anyhow::bail!("sudo cat failed for {}", path.display());
            }

            let content = String::from_utf8(output.stdout)
                .context("Invalid UTF-8 from sudo cat")?;

            content.trim()
                .parse()
                .with_context(|| format!("Failed to parse energy value from {}", path.display()))
        }
        Err(e) => Err(e).with_context(|| format!("Failed to read {}", path.display())),
    }
}

