use crate::power_monitor::{power_reading_subscription, PowerReading};
use cosmic::iced::wayland::popup::{destroy_popup, get_popup};
use cosmic::iced::{Alignment, Limits};
use cosmic::iced_runtime::core::window;
use cosmic::widget;
use cosmic::{Element, Task};

const DISPLAY_ESTIMATE_WATTS: f64 = 11.0;

pub struct PowerMonitorApplet {
    core: cosmic::app::Core,
    popup: Option<window::Id>,
    current_reading: PowerReading,
    display_text: String,
    update_interval_ms: u64,
    show_display_estimate: bool,
}

#[derive(Debug, Clone)]
pub enum Message {
    PowerUpdate(PowerReading),
    TogglePopup,
    PopupClosed(window::Id),
    ToggleDisplayEstimate(bool),
}

impl cosmic::Application for PowerMonitorApplet {
    type Executor = cosmic::executor::Default;
    type Flags = ();
    type Message = Message;
    const APP_ID: &'static str = "com.system76.CosmicAppletPowerMonitor";

    fn core(&self) -> &cosmic::app::Core {
        &self.core
    }

    fn core_mut(&mut self) -> &mut cosmic::app::Core {
        &mut self.core
    }

    fn init(core: cosmic::app::Core, _flags: Self::Flags) -> (Self, Task<cosmic::app::Message<Self::Message>>) {
        let app = PowerMonitorApplet {
            core,
            popup: None,
            current_reading: PowerReading::default(),
            display_text: String::from("C:--W G:--W"),
            update_interval_ms: 330,
            show_display_estimate: true,
        };

        (app, Task::none())
    }

    fn update(&mut self, message: Self::Message) -> Task<cosmic::app::Message<Self::Message>> {
        match message {
            Message::PowerUpdate(reading) => {
                self.current_reading = reading;

                // Update display text
                let cpu_text = if reading.cpu_watts > 0.0 {
                    format!("{:.0}", reading.cpu_watts)
                } else {
                    String::from("--")
                };

                let gpu_text = if let Some(gpu) = reading.gpu_watts {
                    format!("{:.0}", gpu)
                } else {
                    String::from("--")
                };

                self.display_text = format!("C:{}W G:{}W", cpu_text, gpu_text);

                Task::none()
            }

            Message::TogglePopup => {
                if let Some(id) = self.popup.take() {
                    destroy_popup(id)
                } else {
                    let popup_id = window::Id::unique();
                    self.popup = Some(popup_id);

                    let mut popup_settings = self.core.applet.get_popup_settings(
                        self.core.main_window_id().unwrap_or(window::Id::unique()),
                        popup_id,
                        None,
                        None,
                        None,
                    );

                    popup_settings.positioner.size_limits = Limits::NONE
                        .min_width(300.0)
                        .max_width(400.0)
                        .min_height(200.0)
                        .max_height(600.0);

                    get_popup(popup_settings)
                }
            }

            Message::PopupClosed(id) => {
                if self.popup == Some(id) {
                    self.popup = None;
                }
                Task::none()
            }

            Message::ToggleDisplayEstimate(enabled) => {
                self.show_display_estimate = enabled;
                Task::none()
            }
        }
    }

    fn view(&self) -> Element<Self::Message> {
        let text = widget::text(&self.display_text)
            .size(12);

        widget::button::text(text)
            .on_press(Message::TogglePopup)
            .padding(4)
            .into()
    }

    fn view_window(&self, id: window::Id) -> Element<Self::Message> {
        if self.popup == Some(id) {
            self.view_popup()
        } else {
            widget::text("").into()
        }
    }

    fn subscription(&self) -> cosmic::iced::Subscription<Self::Message> {
        power_reading_subscription(self.update_interval_ms).map(Message::PowerUpdate)
    }

    fn on_close_requested(&self, id: window::Id) -> Option<Message> {
        Some(Message::PopupClosed(id))
    }
}

impl PowerMonitorApplet {
    fn view_popup(&self) -> Element<Message> {
        let reading = &self.current_reading;

        let total = reading.cpu_watts
            + reading.gpu_watts.unwrap_or(0.0)
            + if self.show_display_estimate {
                DISPLAY_ESTIMATE_WATTS
            } else {
                0.0
            };

        widget::container(
            widget::column::with_capacity(16)
                .push(widget::text("Power Consumption").size(18))
                .push(widget::horizontal_rule(1))
                .push(widget::vertical_space().height(8))
                .push(power_row("CPU Package", reading.cpu_watts))
                .push(power_row("  CPU Cores", reading.cpu_core_watts))
                .push(power_row("  CPU Uncore", reading.cpu_uncore_watts))
                .push(widget::vertical_space().height(4))
                .push(if let Some(gpu) = reading.gpu_watts {
                    power_row("GPU", gpu)
                } else {
                    widget::text("GPU: Not available").size(14).into()
                })
                .push(widget::vertical_space().height(4))
                .push(if self.show_display_estimate {
                    power_row("Display (est.)", DISPLAY_ESTIMATE_WATTS)
                } else {
                    widget::text("").into()
                })
                .push(widget::horizontal_rule(1))
                .push(
                    widget::row::with_capacity(2)
                        .push(widget::text("Total").size(16))
                        .push(widget::horizontal_space())
                        .push(widget::text(format!("{:.1} W", total)).size(16))
                        .spacing(8),
                )
                .push(widget::vertical_space().height(12))
                .push(
                    widget::row::with_capacity(3)
                        .push(widget::text("Show display estimate").size(14))
                        .push(widget::horizontal_space())
                        .push(widget::toggler(self.show_display_estimate)
                            .on_toggle(Message::ToggleDisplayEstimate))
                        .spacing(8)
                        .align_y(Alignment::Center),
                )
                .spacing(8)
                .padding(16),
        )
        .into()
    }
}

fn power_row(label: &str, watts: f64) -> Element<'static, Message> {
    widget::row::with_capacity(2)
        .push(widget::text(label).size(14))
        .push(widget::horizontal_space())
        .push(widget::text(if watts > 0.0 {
            format!("{:.1} W", watts)
        } else {
            String::from("--")
        }).size(14))
        .spacing(8)
        .into()
}

