use crate::backend::{NvidiaReader, RaplReader};
use futures::stream;
use std::time::{Duration, Instant};

#[derive(Debug, Clone)]
pub struct PowerReading {
    pub cpu_watts: f64,
    pub cpu_core_watts: f64,
    pub cpu_uncore_watts: f64,
    pub gpu_watts: Option<f64>,
    pub timestamp: Instant,
}

impl Default for PowerReading {
    fn default() -> Self {
        Self {
            cpu_watts: 0.0,
            cpu_core_watts: 0.0,
            cpu_uncore_watts: 0.0,
            gpu_watts: None,
            timestamp: Instant::now(),
        }
    }
}

pub fn power_reading_subscription(
    interval_ms: u64,
) -> iced::Subscription<PowerReading> {
    iced::Subscription::run_with_id(
        "power-monitor",
        stream::unfold(
            PowerMonitorState::new(interval_ms),
            |mut state| async move {
                tokio::time::sleep(Duration::from_millis(state.interval_ms)).await;
                let reading = state.read_power().await;
                Some((reading, state))
            },
        ),
    )
}

struct PowerMonitorState {
    rapl: RaplReader,
    nvidia: NvidiaReader,
    interval_ms: u64,
}

impl PowerMonitorState {
    fn new(interval_ms: u64) -> Self {
        let rapl = match RaplReader::new() {
            Ok(reader) => {
                tracing::info!("RAPL reader initialized successfully");
                reader
            }
            Err(e) => {
                tracing::warn!("RAPL initialization failed: {}, using disabled reader", e);
                RaplReader::disabled()
            }
        };

        let nvidia = NvidiaReader::new();

        Self {
            rapl,
            nvidia,
            interval_ms,
        }
    }

    async fn read_power(&mut self) -> PowerReading {
        let timestamp = Instant::now();

        // Read CPU power
        let cpu_power = match self.rapl.read_power().await {
            Ok(power) => power,
            Err(e) => {
                tracing::debug!("RAPL read error: {}", e);
                Default::default()
            }
        };

        // Read GPU power
        let gpu_watts = self.nvidia.read_power().await.ok();

        PowerReading {
            cpu_watts: cpu_power.package_watts,
            cpu_core_watts: cpu_power.core_watts,
            cpu_uncore_watts: cpu_power.uncore_watts,
            gpu_watts,
            timestamp,
        }
    }
}

